let currentSlide=0;const totalSlides=4,slidesToShow=3,maxSlideIndex=1;function moveSlide(e){currentSlide+=e,currentSlide<0?currentSlide=0:currentSlide>1&&(currentSlide=1),updateCarousel()}function updateCarousel(){const e=document.getElementById("carouselTrack");if(!e)return;const t=100/3*-currentSlide;e.style.transform=`translateX(${t}%)`;const r=document.querySelector(".prev-btn"),n=document.querySelector(".next-btn");r&&(r.style.opacity=0===currentSlide?"0.3":"0.7",r.style.cursor=0===currentSlide?"not-allowed":"pointer"),n&&(n.style.opacity=currentSlide>=1?"0.3":"0.7",n.style.cursor=currentSlide>=1?"not-allowed":"pointer")}document.addEventListener("DOMContentLoaded",function(){updateCarousel()});let autoPlayInterval=setInterval(function(){currentSlide<1||(currentSlide=-1),moveSlide(1)},4e3);const carousel=document.querySelector(".simple-carousel");carousel&&(carousel.addEventListener("mouseenter",function(){clearInterval(autoPlayInterval)}),carousel.addEventListener("mouseleave",function(){autoPlayInterval=setInterval(function(){currentSlide<1||(currentSlide=-1),moveSlide(1)},4e3)}));